import React from 'react';
import MenuSection from './MenuSection';

import axios from "axios";

function Menu({ order, setOrder }) {
  const [menuData, setMenuData] = React.useState('');
  const [courses, setCourses] = React.useState('');
  const [items, setItems] = React.useState('');

  React.useEffect(() => {
    axios
    .get(
        `http://localhost:3100/items`, 
    )
    .then(response => {
        console.log(response)
        setMenuData(response.data);
        let _courses = response.data.map(x => isolateCourses(x));
        _courses = removeDuplicates(_courses);
        setCourses(_courses);

    })
    .catch(err => {
        console.log(err)
    });
  }, [setMenuData])

  if (menuData !== '') {
    if (courses.length > 0) {
      console.log(itemsUnderCourse('Sides', menuData))
      return (
        <div>
            <h2>Menu</h2>
            <div>{courses.map((course, index) => (
              <MenuSection name={course} order={order} setOrder={setOrder}/> 
              // <MenuSection name={course} items={itemsUnderCourse(course, menuData)} order={order} setOrder={setOrder}/> 
            ))}
            </div>

        </div>
      )
    } else {
      return (
        <></>
      )
    }

  } else {
    return (
      <div>
          <h2>Menu with hardcoded data</h2>
          <MenuSection name={'Starter'} order={order} setOrder={setOrder}/>
          <MenuSection name={'Main'} order={order} setOrder={setOrder}/>
          <MenuSection name={'Sides'} order={order} setOrder={setOrder}/>
          <MenuSection name={'Drinks'} order={order} setOrder={setOrder}/>
      </div>
    ); 
  }



}

function isolateCourses(datum) {
  return datum.course;
}

function removeDuplicates(array) {
  return array.filter((element, index) => array.indexOf(element) === index);
}

function itemsUnderCourse(course, data) {
  let itemsFound = data.map(datum => itemUnderCourse(datum, course));
  itemsFound = cleanArray(itemsFound); //remove undefineds from itemsFound
  console.log('itemsUnderCourse', itemsFound);
  //return itemsFound;
}

function itemUnderCourse(datum, course) {
  if (datum.course == course) {
    let foundItem = {name: datum.name, price: datum.price};
    return foundItem;
  }
}

function cleanArray(arr) {
  return arr.filter(element => typeof element != undefined);
}


export default Menu;
