import React from 'react';
import DatePicker from './DatePicker';
import TimePicker from './TimePicker';
import axios from "axios";
import { useParams } from 'react-router-dom';

function BookTable({ loggedIn, loginData }) {
  const [chosenDate, setChosenDate] = React.useState();
  const [chosenTime, setChosenTime] = React.useState();
  const [seatingNo, setSeatingNo] = React.useState();



  if (loggedIn) {
    //IF BOOKINGS ALREADY EXIST THEN DISPLAY THEM HERE, USING TERNARY OPERATOR
    return (
      <div>
          <h2>Book Table</h2>

          <div class="Form">
            <div>
              <p>Select a date:</p>
              <br />
              <DatePicker chosenDate={chosenDate} setChosenDate={setChosenDate}/>
            </div>
            <br />
            <div>
              <p>Select a time:</p>
              <br />
              <TimePicker chosenTime={chosenTime} setChosenTime={setChosenTime}/>
            </div>
            <br />
            <div>
              <p>Enter your seating number:</p>
              <input 
                placeholder="e.g 5"
                onChange={(event) => {
                  setSeatingNo(event.target.value);
                }}
                value={seatingNo}
              />
              <br />
            </div>
            <br />
            <button
              onClick={() => {
                handleSubmit(chosenDate, chosenTime, seatingNo);
              }}
              >BOOK
            </button> 
          </div>


      </div>
    ); 
  } else {
    return (
      <div>
        <h3>Please log in to book a table</h3>
      </div>
    )
  }
}





function handleSubmit(chosenDate, chosenTime, seatingNo) {
  console.log(chosenDate, chosenTime, seatingNo);

  axios
  .post(
      `http://localhost:3100/booking`, {
        params: {
          user_id: '$2a$10$OKEfxv1yD0xNpi3MyjhlReqXolRs42bG4Rf8re0f4n2bPHbS9x6lG', 
          date: chosenDate,
          time: chosenTime,
          seating_no: seatingNo
        }
    }
  )
  .then(response => {
    console.log(response);
  })
  .catch(err => {
      console.log(err)
  });

}

export default BookTable;
