import React from 'react';
import DatePicker from './DatePicker';
import TimePicker from './TimePicker';
import Modal from './Modal';

function BookTable({ loggedIn }) {
  const [chosenDate, setChosenDate] = React.useState();
  const [chosenTime, setChosenTime] = React.useState();
  const [seatingNo, setSeatingNo] = React.useState();

  const [alreadyPickedSeatingNo, setAlreadyPickedSeatingNo] = React.useState(false);
  const [showModal, setShowModal] = React.useState(false);
  const [modalMessage, setModalMessage] = React.useState();

  React.useEffect(() => {
    if (seatingNo) {
      setModalMessage('you have entered ' + seatingNo);
    }

  }, [seatingNo])

  React.useEffect(() => {
    if (alreadyPickedSeatingNo) {
      setShowModal(true);
    }
  }, [alreadyPickedSeatingNo])

  if (loggedIn) {
    return (
      <div>
          <h2>Book Table</h2>

          <div class="Form">
            <div>
              <p>Select a date:</p>
              <br />
              <DatePicker chosenDate={chosenDate} setChosenDate={setChosenDate}/>
            </div>
            <br />
            <div>
              <p>Select a time:</p>
              <br />
              <TimePicker chosenTime={chosenTime} setChosenTime={setChosenTime}/>
            </div>
            <br />
            <div>
              <p>Enter your seating number:</p>
              <input 
                placeholder="e.g 5"
                onChange={(event) => {
                  setSeatingNo(event.target.value);
                  setAlreadyPickedSeatingNo(true);
                }}

                value={seatingNo}
              />
              <Modal toggle={showModal} message={modalMessage} />
              <br />
            </div>
            <br />
            <button>BOOK</button> 
          </div>


      </div>
    ); 
  } else {
    return (
      <div>
        <h3>Please log in to book a table</h3>
      </div>
    )
  }
}


