import React from 'react';
import Item from './Item'

function MenuSection({ name, order, setOrder, items }) {

  if (items.length > 0) {
    return (
      <>
        <h2>{name}</h2>
        <div>{items.map((item, index) => (
          // <MenuSection name={course} items={itemsUnderCourse(course, menuData)} order={order} setOrder={setOrder}/>
          <Item name={item.name} price={item.price} order={order} setOrder={setOrder}/>
        ))}
        </div>
      </>
    )
  } else {
     return (
      <div>
          <h2>{name} with hardcoded data</h2>
          <Item name={'Dough Balls'} price={4.99} order={order} setOrder={setOrder}/>
          <Item name={'Garlic Pizza Bread '} price={4.59} order={order} setOrder={setOrder}/>
          <Item name={'Garlic Pizza Bread with Mozzarella'} price={4.99} order={order} setOrder={setOrder}/>
          <Item name={'Garlic Bread with Vegan Mozzarella '} price={4.99} order={order} setOrder={setOrder}/>
      </div>
    ); 
  }


  }

export default MenuSection;
