import React from 'react';
import DatePicker from './DatePicker';
import TimePicker from './TimePicker';

function BookTable({ loggedIn }) {
  const [chosenDate, setChosenDate] = React.useState();
  const [chosenTime, setChosenTime] = React.useState();
  const [seatingNo, setSeatingNo] = React.useState();

  if (loggedIn) {
    return (
      <div>
          <h2>Book Table</h2>

          <div class="Form">
            <div>
              <p>Select a date:</p>
              <br />
              <DatePicker chosenDate={chosenDate} setChosenDate={setChosenDate}/>
              {/* <input type="text" value={date} onChange={(event) => {setDate(event.target.value)}}/> */}
            </div>
            <br />
            <div>
              <p>Select a time:</p>
              <br />
              <TimePicker chosenTime={chosenTime} setChosenTime={setChosenTime}/>
              {/* <input type="text" value={time} onChange={(event) => {setTime(event.target.value)}} /> */}
            </div>
            <br />
            <div>
              <p>Enter your seating number:</p>
              <input 
                placeholder="e.g 5"
                onChange={(event) => {
                  setSeatingNo(event.target.value);
                }}

                value={seatingNo}
              />
              <br />
              {/* <input type="text" value={seatingNo} onChange={(event) => {setSeatingNo(event.target.value)}} /> */}
            </div>
            <br />
            <button>BOOK</button> 
          </div>


      </div>
    ); 
  } else {
    return (
      <div>
        <h3>Please log in to book a table</h3>
      </div>
    )
  }
}


function selectedSeatingNo() {

}



function handleSubmit() {
  console.log('BOOKED!')
}

export default BookTable;
