import React from 'react';
import logo from './logo.svg';
import './App.css';

import Menu from './Components/Menu';
import BookTable from './Components/BookTable';
import ContactUs from './Components/ContactUs';
import Home from './Components/Home';
import CurrentOrder from './Components/CurrentOrder';
import NavBar from './Components/NavBar'
import Login from './Components/Login';

import {
  BrowserRouter as Router,
  Switch,
  Route
  // Link,
  // useHistory
} from "react-router-dom";


function App() {
  const [order, setOrder] = React.useState([]);
  const [loggedIn, setLoggedIn] = React.useState(false);


  return (
    <div className="App">
      <h1>Le Restaurant</h1>

      {/* if logged in, then display 'logged in as... and log out btn', if not then show login form */}
      <Login loggedIn={loggedIn} setLoggedIn={setLoggedIn} />
      <br />
      <Router>
        <NavBar />
        <Switch>
        <Route path="/contact-us">
            <ContactUs />
          </Route>
          <Route path="/current-order">
            <CurrentOrder order={order} setOrder={setOrder} loggedIn={loggedIn}/>
          </Route>
          <Route path="/book-table">
            <BookTable loggedIn={loggedIn}/>
          </Route>
          <Route path="/menu">
            <Menu order={order} setOrder={setOrder}/>
          </Route>
          <Route path="/">
            <Home />
          </Route>
        </Switch>

      </Router>
    </div>
  );
}

export default App;
