import React from 'react';

import axios from "axios";

function Login({ loggedIn, setLoggedIn }) {
    const [loginData, setLoginData] = React.useState();
    const [emailInput, setEmailInput] = React.useState();
    const [passwordInput, setPasswordInput] = React.useState();
    const [usersData, setUsersData] = React.useState();

    if (loggedIn) {
        return (
            <div>
                <h3>Logged in as {loginData.first_name} {loginData.last_name} | {loginData.email}</h3>
                <button>Log out</button>
            </div>
        )
    } else {
        return (
            <div>
                <h3>Login form</h3>
                <div class="form">
                    <input 
                        placeholder="email address"
                        onChange={(event) => {
                            setEmailInput(event.target.value);
                        }}

                        value={emailInput}
                    />

                    <input 
                        placeholder="password"
                        onChange={(event) => {
                            setPasswordInput(event.target.value);
                        }}

                        value={passwordInput}
                    />

                    <button
                        onClick={async () => {
                            console.log('email: ', emailInput);
                            //do axios request to validate user
                            axios
                            .get(
                                `http://localhost:3100/users`, 
                            )
                            .then(response => {
                                console.log(response)
                                setUsersData(response.data);
                                if (findCredentials(response.data, emailInput, passwordInput)) {
                                    let index = findCredentials(response.data, emailInput, passwordInput);
                                    console.log('credentials exist, index: ' + index);
                                    // let credentialsObj = {};
                                    // credentialsObj.email = emailInput;
                                    // credentialsObj.first_name
                                } else {
                                    console.log('credentials DO NOT exist');
                                }
                            })
                            .catch(err => {
                                console.log(err)
                            });
                            //if correct then set the LoginData and set Loggedin to True and the display will change
                            //else show modal saying this is incorrect
                        }}
                        >Log in
                    </button>
                </div>
            </div>
        ); 
    }


}

function findCredentials(data, emailInput, passwordInput) {
    let result = data.map(datum => matchedCredentials(datum, emailInput, passwordInput));
    //console.log('result', result);
    if (result.includes(true)) {
        let index = result.indexOf(true);
        return index;
    } else {
        return false;
    }
}

function matchedCredentials(datum, emailInput, passwordInput) {
    if (datum.email === emailInput && datum.password === passwordInput) {
        return true;
    } else {
        return false;
    }
}

export default Login;
